// Define some example variables with TypeScript types



// Number variable
const age: number = 30;

// Boolean variable
const isActive: boolean = true;

// Array of strings
const hobbies: string[] = ["Reading", "Traveling", "Gaming"];

// Tuple
const user: [number, string] = [1, "Jane Doe"];

// Enum
enum Role {
    Admin,
    User,
    Guest,
}

// Object
const person: { id: number; name: string; role: Role } = {
    id: 1,
    name: "Alice",
    role: Role.Admin,
};

// Any type
let randomValue: any = "Hello";
randomValue = 42;

// Void function
function logMessage(message: string): void {
    console.log(message);
}